$(document).ready(function(){var a=`<div class="qPicker"><div class="yearWrap"><div class="yearHead">Year</div><div class="yearValue"><select name="yearValue"></select></div></div><div class="qWrap"><div class="quarter quarter1" data-class="quarter" data-value="q1">Q1</div><div class="quarter quarter2" data-class="quarter" data-value="q2">Q2</div><div class="quarter quarter3" data-class="quarter" data-value="q3">Q3</div><div class="quarter quarter4" data-class="quarter" data-value="q4">Q4</div></div></div>`;$("body").append(a);for(var i=1980;i<=2100;i++){$('.yearValue select').append('<option value="'+i+'">'+i+'</option>');}$('.quarterInput input').focus(function(){$('.qPickerParent .qPicker').remove();$('.qPickerParent').append($('.qPicker').clone());$('.qPickerParent .qPicker').css('top',$('.quarterInput').height()).show();$('.qPickerParent .qPicker .yearValue select').val(new Date().getFullYear());});$(document).on('click','.qPickerParent .quarter',function(){$('.quarterInput input').val($(this).attr('data-value')+'/'+$('.qPickerParent .qPicker').find('.yearValue select').val());$('.qPickerParent .qPicker').hide();});$('body').click(function(){$('.qPickerParent .qPicker').hide();});$('.qPickerParent').click(function(event){if(event.target.getAttribute('data-class')!='quarter'){event.stopPropagation();}});});